def get_url_from_column(tr, column_id):
    a = tr.find('td', {"class": column_id}).find('a')
    return None if not a else requests.get(a["href"]).url


def get_html_table():
    html = requests.get("https://climatecompatiblegrowth.com/starter-kits/").text
    soup = BeautifulSoup(html, 'html.parser')
    return soup.find("table", { "id":"tablepress-1" })


def get_table_rows(table):
    tbody = table.find('tbody')
    return tbody.select('tr')


def create_download_url(tr, column_id, country, data_type):
    a = tr.find('td', {"class": column_id}).find('a')
    if not a:
        return None
    table_url = requests.get(a["href"]).url
    idx = table_url.find('#')
    partial_url = table_url #if idx <= 0 else table_url[::idx]
    return partial_url[0:33]


def get_progress(count, total):
    completion_ratio = (count + 1)/total
    return round(completion_ratio * 100, 2)


