def create_dics(dic_name):
    myfile=open(dic_name,'r').read().splitlines()
    dic_countries={}
    dic_scenarios={}
    for count in range(1, len(myfile)):
        country=myfile[count].split(',')[0]
        dic_countries[country]=myfile[count].split(',')[1]
        dic_scenarios[country]=myfile[count].split(',')[2]
    return dic_countries, dic_scenarios


def get_filenames(scenario, dic_name):
    en_urls, tr_urls = run(scenario)
    print(en_urls)
    dic_countries, dic_scenarios=create_dics(dic_name)
    filenames={}
    for country in en_urls.keys():
        for filename in en_urls[country]:
            scen=dic_scenarios[country] if scenario=='as' else scenario
            if fnmatch.fnmatch(filename, scen.join(["*","*"])):
                filenames[country]=filename
    return filenames

